import React, {useRef, useEffect, useState} from 'react';
import mapboxgl from 'mapbox-gl';

export default function App() {
	
mapboxgl.accesToken = 'pk.eyJ1Ijoib2xpc2hyIiwiYSI6ImNrZjVwZWg5NzBveXozMW9mZ2V1bXl2MHYifQ.aetx6rI5xii820zCaRyYYA';

const mapContainer = useRef(null);
const map = useRef(null);
const [lng, setLng] = useState(45.21329659527082);
const [lat, setLat] = useState(13.674078945516115);
const [zoom, setZoom] = useState(14);

useEffect(() => {
	if (map.current) return;
	map.current = new mapboxgl.Map({
		container: mapContainer.current,
		style: 'mapbox://styles/mapbox/streets-v11',
		center: [lng, lat],
		zoom: zoom,
		token: mapboxgl.accesToken
	});
	
});

	return (
	<div>
		<div ref={mapContainer} className='map-container' />
	</div>
);

}
